import "./Welcome.scss";

const Welcome = ({ onClick }) => {
  return (
    <div className="welcome">
      <h1 className="simon">SIMON</h1>
      <h2>Get ready to watch, remember, repeat!</h2>
      <div>
        <p>
          Get ready to watch, remember, repeat! The Simon game is the exciting
          electronic game of lights and sounds in which players must repeat
          random sequences of lights by pressing the colored pads in the correct
          order. Keep track of your score as you challenge friends or try to
          beat your own high score. Includes Simon game unit and instructions.
        </p>
      </div>

      <button className="button" onClick={onClick}>
        Play
      </button>
    </div>
  );
};
export default Welcome;
