//Frontend
import { useEffect, useState } from "react";
import "./App.scss";
import ColorCard from "./components/ColorCard";
import timeout from "./utils/util";

const App = () => {
  // const [backendData, setBackendData] = useState([{}]);

  // useEffect(() => {
  //   fetch("/api")
  //     .then((response) => response.json())
  //     .then((data) => setBackendData(data));
  // }, []);

  const [isOn, setIsOn] = useState(false);

  const colorList = ["green", "red", "yellow", "blue"];
  const initPlay = {
    isDisplay: false,
    colors: [],
    score: 0,
    userPlay: false,
    userColors: [],
  };
  const [play, setPlay] = useState(initPlay);
  const [flashColor, setFlashColor] = useState("");

  const startHandle = () => {
    setIsOn(true);
  };

  useEffect(() => {
    if (isOn) {
      setPlay({ ...initPlay, isDisplay: true });
    } else {
      setPlay(initPlay);
    }
  }, [isOn]);

  useEffect(() => {
    if (isOn && play.isDisplay) {
      let newColor = colorList[Math.floor(Math.random() * 4)];
      const copyColors = [...play.colors,newColor];
      // copyColors.push(newColor);
      console.log("copy",copyColors)
      setPlay({ ...play, colors: copyColors });
    }
  }, [isOn, play.isDisplay]);

  useEffect(() => {
    // console.log("useeffect",play.colors.length)
    if (isOn && play.isDisplay && play.colors.length) {
      displayColors();
    }
  }, [isOn, play.isDisplay, play.colors.length]);

  const displayColors = async () => {
    await timeout(1000);
    //  console.log("color:",play.colors)
    //  console.log("colorlength:",play.colors.length)
    for (let i = 0; i < play.colors.length; i++) {
      setFlashColor(play.colors[i]);
      await timeout(1000);
      setFlashColor("");
      await timeout(1000);

      if (i === play.colors.length - 1) { // last color
        const copyColors = [...play.colors];
        setPlay({
          ...play,
          isDisplay: false,
          userPlay: true,
          userColors: copyColors.reverse(),
        });
      }
    }
  };

  const cardClickHandler = async (color) => {
    if (!play.isDisplay && play.userPlay) {
      const copyUserColors = [...play.userColors];
      const lastColor = copyUserColors.pop(); // remove the last from the array

      setFlashColor(color);

      if (color === lastColor) {
        if (copyUserColors.length) {
          setPlay({ ...play, userColors: copyUserColors });
        } else {
          await timeout(1000);
          setPlay({
            ...initPlay,
            isDisplay: true,
            userPlay: false,
            score: play.colors.length,
            userColors: [],
          });
        }
      } else {
        await timeout(1000);
        setPlay({ ...initPlay, score: play.colors.length });
      }
      await timeout(1000);
      setFlashColor("");
    }
  };

  return (
    <div className="App">
      {/* <header className="App-header"> */}
      <div className="cardWarpper">
        {colorList.map((color, index) => (
          <ColorCard
            key={index}
            color={color}
            flash={flashColor === color}
            onClick={() => {
              cardClickHandler(color);
            }}
          ></ColorCard>
        ))}
      </div>
      {/* </header> */}

      {!isOn && !play.score && (
        <button className="startButton" onClick={startHandle}>
          Start
        </button>
      )}
      {isOn && (play.isDisplay || play.userPlay) && (
        <div className="score">{play.score}</div>
      )}
    </div>
  );
};

export default App;
