//Frontend
import { useEffect, useState } from "react";
import "./App.scss";
import Button from "./components/Button";
import ColorCard from "./components/ColorCard";
import timeout from "./utils/util";
import soundFile from "./sounds/simonSound1";

const App = () => {
  const [isOn, setIsOn] = useState(false);
  const colorList = ["red", "green", "blue", "yellow"];
  const [audioPlayer, setAudioPlayer] = useState(new Audio(soundFile));

  const initPlay = {
    isDisplay: false,
    colors: [],
    score: 0,
    userPlay: false,
    userColors: [],
    highScore: 0,
  };
  const [play, setPlay] = useState(initPlay);
  const [flashColor, setFlashColor] = useState("");

  // const [highScore, ] = useState(() => {
  //   const highScoreFromStorage = localStorage.getItem("highScore");
  //   if (highScoreFromStorage) {
  //     return highScoreFromStorage;
  //   }
  //   return 0;
  // });

  const startHandle = () => {
    setIsOn(true);
  };

  useEffect(() => {
    fetch("http://localhost:5000/api/status", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ isOn: isOn, score: play.score }),
    })
      .then((response) => response.json())
      .then((res) => {
        play.score = res.score;
        play.highScore = res.highScore;
      })
      .catch((error) => console.log(error));
  }, [isOn]);

  useEffect(() => {
    if (isOn) {
      setPlay({ ...initPlay, isDisplay: true });
    } else {
      setPlay(initPlay);
    }
  }, [isOn]);

  useEffect(() => {
    if (isOn && play.isDisplay) {
      let newColor = colorList[Math.floor(Math.random() * 4)];
      let copyColors = [...play.colors];
      copyColors.push(newColor);
      setPlay({ ...play, colors: copyColors });
    }
  }, [isOn, play.isDisplay]);

  useEffect(() => {
    if (isOn && play.isDisplay && play.colors.length) {
      displayColors();
    }
  }, [isOn, play.isDisplay, play.colors.length]);

  const displayColors = async () => {
    await timeout(1000);
    for (let i = 0; i < play.colors.length; i++) {
      console.log("play.colors:", play.colors);
      setFlashColor(play.colors[i]);
      await timeout(1000);
      setFlashColor("");
      await timeout(1000);
    }

    const copyColors = [...play.colors];
    setPlay({
      ...play,
      isDisplay: false,
      userPlay: true,
      userColors: copyColors.reverse(),
    });
  };

  const cardClickHandle = async (color) => {
    audioPlayer.play();
    if (!play.isDisplay && play.userPlay) {
      const copyUserColors = [...play.userColors];
      const lastColor = copyUserColors.pop(); // remove the last from the array

      setFlashColor(color);

      if (color === lastColor) {
        if (copyUserColors.length) {
          setPlay({ ...play, userColors: copyUserColors });
        } else {
          await timeout(1000);
          setPlay({
            ...play,
            isDisplay: true,
            userPlay: false,
            score: play.colors.length,
            userColors: [],
          });
        }
      } else {
        await timeout(1000);
        setPlay({ ...initPlay, score: play.colors.length });
      }
      await timeout(1000);
      setFlashColor("");
    }
  };
  const closeHandle = () => {
    setIsOn(false);

    // const highScore = localStorage.getItem("highScore");
    // if (!highScore || play.score - 1 > highScore) {
    //   console.log("play.score - 1 ",play.score - 1 )
    //   setHighScore(play.score - 1);
    //   localStorage.setItem("highScore", JSON.stringify(play.score - 1));
    // }
  };

  return (
    <div className="App">
      <div className="cardWarpper">
        {colorList &&
          colorList.map((color, index) => (
            <ColorCard
              key={index}
              color={color}
              flash={flashColor === color}
              onClick={() => {
                cardClickHandle(color);
              }}
            ></ColorCard>
          ))}
      </div>

      <div className="centerWrapper">
        {!isOn && <h1 className="simon">SIMON</h1>}
        {!isOn && !play.score && <Button onClick={startHandle} text="Start" />}
        {isOn && (play.isDisplay || play.userPlay) && (
          <div className="score">{play.score}</div>
        )}

        {isOn && !play.isDisplay && !play.userPlay && play.score && (
          <div className="lost">
            <div className="score">Score:{play.score}</div>
            <Button onClick={closeHandle} text="Close" />
          </div>
        )}
      </div>
      <div className="highScoreWrapper">
        <h1>Highest:{play.highScore}</h1>
      </div>
      <div className="turnWrapper">
        {isOn && <h1>{play.isDisplay ? "Computer turn" : "Your turn"}</h1>}
      </div>
    </div>
  );
};

export default App;
